import update from "immutability-helper";
import { rockPaperScissors, computerGuessToWords } from "../utils/index"

import {
  RESTART_GAME,
  TOGGLE_HELP,
  GET_GUESS_REQUEST,
  GET_GUESS_SUCCESS,
  GET_GUESS_FAILURE,
  HANDLE_INPUT,
} from '../actions/'

const INITIAL_STATE = {
  bestOf: 1,
  timesWrong: 0,
  timesRight: 0,
  guess: "",
  computerGuess: "",
  helpOpen: false,
  error: null,
  loading: false
};

export default (state = INITIAL_STATE, action) => {

  switch (action.type) {
    case TOGGLE_HELP:
      return update(state, {
        helpOpen: { $set: !state.helpOpen }
      });
    case GET_GUESS_REQUEST:
      return update(state, {
        loading: { $set: true }
      });
    case GET_GUESS_SUCCESS:
      let computerWord = computerGuessToWords(action.payload.random_number)
      let result = rockPaperScissors(state.guess, computerWord);
      if (result) {
        return update(state, {
          loading: { $set: false },
          timesRight: { $set: state.timesRight++ },
          computerGuess: { $set:  computerWord}
        })
      } else {
        return update(state, {
          loading: { $set: false },
          timesWrong: { $set: state.timesWrong++ }
        })
      }
      break
    case GET_GUESS_FAILURE:
      return update(state, {
        loading: { $set: false },
        error: { $set: action.payload.error || "There was an issue retrieving the computer's guess" }
      });
    case HANDLE_INPUT:
      return update(state, {
        guess: { $set: action.payload }
      });
    case RESTART_GAME:
      return update(state, {
        bestOf: { $set: 1 },
        timesWrong: { $set: 0 },
        timesRight: { $set: 0 },
        guess: { $set: "" },
        helpOpen: { $set: false },
        error: { $set: null },
        loading: { $set: false },
      })

    default:
      return state;
  }
}
