import update from "immutability-helper";
import { winOrLose, rightOrWrong } from "../utils/index"

import {
  RESTART_GAME,
  TOGGLE_HELP,
  GET_GUESS_REQUEST,
  GET_GUESS_SUCCESS,
  GET_GUESS_FAILURE,
  HANDLE_INPUT,
} from '../actions/'

const INITIAL_STATE = {
  bestOf: 1,
  timesWrong: 0,
  timesRight: 0,
  guess: "",
  lastGuessCorrect: null,
  result: "Make a Guess!",
  winOrLose: "",
  helpOpen: false,
  error: null,
  loading: false
};

export default (state = INITIAL_STATE, action) => {

  switch (action.type) {
    case TOGGLE_HELP:
      return update(state, {
        helpOpen: { $set: !state.helpOpen }
      });
    case GET_GUESS_REQUEST:
      return update(state, {
        loading: { $set: true }
      });
    case GET_GUESS_SUCCESS:
      let feedback, computerGuess;
      computerGuess = action.payload.random_number;
      let guessResult = rockPaperScissors(state.guess, computerGuess)
