      return update(state, {
        loading: { $set: false },
        error: { $set: action.payload.error || "There was an issue retrieving the computer's guess" }
      });
    case HANDLE_INPUT:
      return update(state, {
        guess: { $set: action.payload }
      });
    case RESTART_GAME:
      return update(state, {
        bestOf: { $set: 1 },
        timesWrong: { $set: 0 },
        timesRight: { $set: 0 },
        lastGuessCorrect: { $set: null },
        helpOpen: { $set: false },
        guess: { $set: "" },
        error: { $set: null },
        loading: { $set: false },
      })

    default:
      return state;
  }
}
