export const winOrLose = (timesRight, timesWrong, bestOf) => {
  if (timesRight > bestOf / 2) {
    return "You win!"
  }
  if (timesWrong > bestOf / 2) {
    return "You lose!"
  }
  return ""
};

export const rightOrWrong = lastGuessCorrect => {
  switch (lastGuessCorrect) {
    case true:
      return "Right!"
    case false:
      return "Wrong!"
    default:
      return "Make A Guess!"
  }
}

export const computerGuessToWords = num => {
  switch (num) {
    case num >= 1 && num <= 20:
      return "Rock"
    case num >= 21 && num <= 40:
      return "Paper"
    case num >= 41 && num <= 60:
      return "Scissors"
    case num >= 61 && num <= 80:
      return "Lizard"
    case num >= 81 && num <= 100:
      return "Spock"
    default:
      return ""
  }

}

