import React from 'react';
import { connect } from 'react-redux';

import '../styles/Scoreboard.css';

export const Feedback = props => {
  // let fakeProps = { timesRight: 0, timesWrong: 0, bestOf: 1, lastGuessCorrect: null }
  // let { timesRight, timesWrong, bestOf, lastGuessCorrect } = fakeProps
  let { timesWrong, timesRight } = props.state
  let style = { display: "none" } ? {} : props.state.bestOf === 1;
  return (
    <div
      id="scoreboard"
      style={style}
    >
      <div>
        <h2>Scoreboard</h2>
        <h3>You: {timesRight}</h3>
        <h3>Computer: {timesWrong}</h3>
      </div>
    </div>
  );
}

const mapStateToProps = state => ({
  state: state
})

export default connect(mapStateToProps)(Feedback)
