import React from 'react';
import { connect } from 'react-redux';

import '../styles/Feedback.css';

export const Feedback = props => {
  let winOrLose;
  let fakeProps = { timesRight: 0, timesWrong: 1, bestOf: 1, result: "wrong" }
  // let { timesRight, timesWrong, bestOf, result } = this.props
  let { timesRight, timesWrong, bestOf, result } = fakeProps

  if (timesRight > bestOf / 2) {
    winOrLose = "Win"
  }
  if (timesWrong > bestOf / 2) {
    winOrLose = "Lose"
  }
  return (
    <h2
      id="feedback"
      role="status"
      aria-live="assertive"
      aria-atomic="true"
    >
      {result} {winOrLose}
    </h2>
  );
}

